import { FETCH_POSTS, NEW_POST } from '../actions/types'

const initialState = {
  items: [], // represents initial array
  item: {}, // represents single post when we get the response back
}

export default function(state = initialState, action) {
  switch (action.type) {
    default:
      return state;
  }
}
